jQuery(document).ready(function($) {
    let dataTables_paginate = $('#sample_3_paginate');

    checkDisabled()
    $("select[name='sample_3_length']").change(function (){
        checkDisabled()
    })

    function checkDisabled(){
        if ($('#sample_3_paginate .prev.disabled').length === 1
            && $('#sample_3_paginate .next.disabled').length === 1){
            dataTables_paginate.hide();
            return;
        }
        dataTables_paginate.show();
    }
});

